mapper.select:
select a.id, a.name, u1.name owner, a.start_date startDate, a.end_date endDate, u2.create_by createBy, u3.edit_by editBy from tbl_activity a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like "%"#{name}"%"
and u1.name like "%"#{owner}"%"
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.start_date desc

select count(*) from tbl_activity a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like "%"#{name}"%"
and u1.name like "%"#{owner}"%"
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
