mapper.select:
select c.id id, v2.value appellation, c.fullname fullname, u1.name owner, c.company company , c.job job, c.email email, c.phone phone, c.website website, c.mphone mphone, v1.value state, v3.value source, c.description description, c.contact_summary contactSummary, c.next_contact_time nextContactTime, c.address address, u2.name createBy, c.create_time createTime, u3.name editBy, c.edit_time editTime from tbl_clue c join tbl_user u1 on c.owner = u1.id join tbl_user u2 on c.create_by = u2.id join tbl_dic_value v1 on c.state = v1.id join tbl_dic_value v2 on c.appellation = v2.id join tbl_dic_value v3 on c.source = v3.id left join tbl_user u3 on c.edit_by = u3.id
and c.fullname like "%"#{fullname}"%"
and u1.id = #{owner}
and c.company like "%"#{company}"%"
and c.phone like "%"#{phone}"%"
and c.mphone like "%"#{mphone}"%"
and v1.state = #{state}
and v3.source = #{source}
