mapper.select:
select c.id id, u1.name owner, v1.value source, c1.name customerId, c.fullname fullname, v2.value appellation, c.email email, c.mphone mphone, c.job job, u2.name createBy, c.create_time createTime, u3.name editBy, c.edit_time editTime, c.description description, c.contact_summary contactSummary, c.next_contact_time nextContactTime, c.address address, c.birth birth from tbl_contacts c join tbl_user u1 on c.owner=u1.id join tbl_user u2 on c.create_by=u2.id join tbl_dic_value v1 on c.source=v1.id join tbl_dic_value v2 on c.appellation=v2.id join tbl_customer c1 on c.customer_id=c1.id left join tbl_user u3 on c.edit_by=u3.id
and c.owner=#{owner}
and c.fullname like "%"#{fullname}"%"
and c1.name like "%"#{customerId}"%"
and v1.id=#{source}
and c.birth=#{birth}

mapper.delete:
delete from tbl_contacts
where id in

    #{id}
